import React from 'react'
import { Button } from 'semantic-ui-react'

class Count extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            count: 0
        }
    }

    increment = state => {
        this.setState({count: state.count + 1})
    }

    render(){
        return (
            <div>
                <Button onClick={() => this.props.playSound(), this.increment()} />
            </div>
        )
    }
}

export default Count;
