import React from 'react'
import { connect } from 'react-redux'
import { updateLoginForm } from '../actions/loginForm.js'

const Login = ({loginForm, updateLoginForm}) => {

    const handleOnChange = e => {
        const {name, value} = e.target 
        const formData = {
            ...loginForm,
            [name]: value
        }
        updateLoginForm(formData)
    }
    render()
        return (
            <form onSubmit={undefined}>
                <input name="email" value={loginForm.email} onChange={handleOnChange} />
                <input name="password" value={loginForm.password} onChange={handleOnChange} />
                <input type="submit" value="Login" />
            </form>
        )
}

//returns a props object from store with a key loginForm and values: {email: "", password: ""} that can be destructured
const mapStateToProps = state => {
    return {
        loginForm: state.loginForm
    }
}

export default connect(mapStateToProps, {updateLoginForm})(Login)
