import React from 'react'
import { connect } from 'react-redux'
import { Modal, Form, Button } from 'semantic-ui-react'
import { updateLoginForm } from '../actions/loginForm.js'

const Login = ({loginForm, updateLoginForm}) => {

    const handleOnChange = e => {
        const {name, value} = e.target 
        const formData = {
            ...loginForm,
            [name]: value
        }
        updateLoginForm(formData)
    }


    return (
        <div className="formInfo">
            <Form unstackable>
                <Form.Group widths={2}>
                    <Form.Input label='Email' placeholder='Email' value={loginForm.email} onChange={handleOnChange} />
                    <Form.Input label='Pasword' placeholder='Password' value={loginForm.password} onChange={handleOnChange} />
                </Form.Group>
                <Form.Checkbox label='I agree to the Terms and Conditions' />
                <Button type='submit' onSubmit={undefined}>Submit</Button>
            </Form>
        </div>
    )
}

//returns a props object from store with a key loginForm and values: {email: "", password: ""} that can be destructured
const mapStateToProps = state => {
    return {
        loginForm: state.loginForm
    }
}

export default connect(mapStateToProps, {updateLoginForm})(Login)
