import React from 'react'
import { connect } from 'react-redux'
import { filterAudiocards } from '../actions/myAudioCards'
import Audiocard from './Audiocard'

class Searchbar extends React.Component {
    constructor({audiocards, searchText}) {
      super()
      this.state = {
        audiocards,
        searchText,
        }
    }


    render(){
        const getMatchingCards = this.state.audiocards.filter(audio => {
            return audio.attributes.tags.includes(this.state.searchText)
        })

        const audiocards = getMatchingCards.map(audio => 
            <Audiocard audiocard={audio} key={audio.id} />
        )

        return(
            <div>
                {audiocards}
            </div>
        )
    }
}

const mapStateToProps = state => {

    return {
        audiocards: state.myAudioCards,
        searchText: state.searchbar
    }
}


export default connect(mapStateToProps, {filterAudiocards})(Searchbar)
