import React, { PureComponent } from 'react';
import { Menu, Icon, Search, Button, Modal } from 'semantic-ui-react';
import { Link } from 'react-router-dom';
import Login from './components/Login'
import './App.css';
import './images/userIcon.png';


class NavBar extends PureComponent {
  constructor(props) {
    console.log(props.userStatus)
    super()
    this.state = {
      deviceWidth: window.innerWidth,
      userStatus: props.userStatus
    }
  }


  //resizes based on device type
  componentDidMount() {
    window.addEventListener('resize', this.handleWindowSizeChange);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleWindowSizeChange);
  }

  handleWindowSizeChange = () => {
    this.setState({ deviceWidth: window.innerWidth });
  }


  render(){
    //resizing
    let isMobile
    const setWidth = this.state.deviceWidth
    setWidth <= 750 ?  isMobile = true : isMobile = false


    let resizedIcon
    let navType
    if (isMobile) {
      resizedIcon = "small"
      navType = "navMobile"
    } else {
      resizedIcon = "huge"
      navType = "navDesktop"
    }

    return (
        <div className={navType}>
            <Menu secondary fixed="top" widths={4}>
                <Menu.Item><Icon size={resizedIcon} name='home' color='orange' /></Menu.Item>
                <Menu.Item>
                    <Search category />
                </Menu.Item>
                <Menu.Item>
                    <Link to='/recorder' ><Icon size={resizedIcon} name='microphone' color='olive' /></Link>
                </Menu.Item>
                <Menu.Item className='loginButtons'>
                    <Button.Group>
                        <Modal onClose={this.close}
                            size='small' 
                            dimmer='blurring' 
                            trigger={
                                <Button color='teal' className='loginButton' disabled={this.state.userStatus}>
                                    <Link to='/login'>Login</Link>
                                </Button>} 
                            closeIcon>
                            <Modal.Content> 
                                <Login /> 
                            </Modal.Content>
                        </Modal>   
                        <Button.Or />
                        <Button color='grey'>
                          <Link to='/logout'>Logout</Link>
                        </Button>
                    </Button.Group>
                </Menu.Item>
            </Menu>
        </div>

    )

  }

}

export default NavBar;
