import React, {useState} from 'react'
import {Icon, Label} from 'semantic-ui-react'

const Playcontroller = ({playing, playOrPause, sound, playCount}) => {

    const [state, setState] = useState({
        loop: false
    })

    const handleLoop = e => {
        console.log("LOOPING")
        if(sound){
            sound.loop ? sound.loop = false : sound.loop = true
        }
    }

    const handleRewind = e => {
        if(sound){
            sound.stop()
            sound.play()
        }
    }

    const handleFastforward = e => {
        if(sound){
            sound.stop()
        }
    }

    return(
        <div>
            <Label className="count" circular size='large'>
                {playCount}
            </Label> 
            <Icon 
                onClick={handleLoop}
                style={state.loop ? {color:"#D8ECE8"} : {color:"D68A5F"}}
                name='retweet'
                size='tiny'
            />  
            <Icon 
                onClick={handleRewind}
                name='fast backward'
                size='small'
            />   
            <Icon 
                onClick={playOrPause} 
                name={playing ? 'pause circle outline':'play circle outline'} 
                style={{color: "#D8ECE8"}}
                count={playCount}
            />
            <Icon 
                onClick={handleFastforward}
                name='fast forward'
                size='small'
            />  
        </div>
    )

}

export default Playcontroller;
