import React, { PureComponent } from 'react';
import { Menu, Icon } from 'semantic-ui-react'
import { Link } from 'react-router-dom';

class NavBar extends PureComponent {
  constructor(props) {
    super()
    this.state = {
      deviceWidth: window.innerWidth
    }
  }

  //resizes based on device type
  componentDidMount() {
    window.addEventListener('resize', this.handleWindowSizeChange);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleWindowSizeChange);
  }

  handleWindowSizeChange = () => {
    this.setState({ deviceWidth: window.innerWidth });
  }

  render(){
    let isMobile
    const setWidth = this.state.deviceWidth
    setWidth <= 750 ?  isMobile = true : isMobile = false


    let resizedIcon
    let navType
    if (isMobile) {
      resizedIcon = "small"
      navType = "navMobile"
    } else {
      resizedIcon = "huge"
      navType = "navDesktop"
    }


    return (
      <div className={navType}>
          <Menu fixed="top" widths={4}>
            <Menu.item>
                <Icon size={resizedIcon} color='purple'>Home</Icon>
            </Menu.item>
            <Menu.item>
                {/* <Link to='/' ><Icon size={resizedIcon} color='purple'>Home</Icon></Link> */}
            </Menu.item>
            <Menu.item>
                {/* <Link to='/' ><Icon size={resizedIcon} color='purple'>Home</Icon></Link> */}
            </Menu.item>
            <Menu.item>
                {/* <Link to='/' ><Icon size={resizedIcon} color='purple'>Home</Icon></Link> */}
            </Menu.item>
          </Menu>
      </div>
    )

  }




}

export default NavBar;
