import { useBooleanKnob } from '@stardust-ui/docs-components'
import React from 'react'
import { Image, Card, Grid, Button, Sidebar, Icon, Menu} from 'semantic-ui-react';
import myAudioCards from '../reducers/myAudioCards';
import { connect } from 'react-redux'
import Tags from './Tags'
import Pizzicato from 'pizzicato'
import {confirmDelete, deleteAudiocard} from '../actions/myAudioCards'

const Audiocard = ({audiocard}) => {

    let newDate = ''
    if (audiocard.attributes.created_at){
        let convertDate = audiocard.attributes.created_at.split('T')[0].split('-')
        newDate = new Date(convertDate).toDateString()
    }

    //play on hover
    const playRecording = () => {
        if(audiocard.attributes.soundfile){
            const getSound = new Pizzicato.Sound({
                source: 'file',
                options: {path: audiocard.attributes.soundfile}
            }, () => {
                // getSound.play()
            })
        }
    }

    const tags = JSON.parse(audiocard.attributes.tags)

    const getTags = tags.map((tag, index) => 
        <Tags tag={tag} key={index} />
    )

    const [visible, setVisible] = useBooleanKnob({name: 'visible'})



        return (
                <Sidebar.Pushable as={Card}>
                    <Sidebar
                        as={Menu}
                        animation='overlay'
                        icon='labeled'
                        onHide={() => setVisible(false)}
                        vertical 
                        visible={visible}
                        width='thin'
                    >
                     <Menu.Item as='a'>
                        <Icon name='home' />
                        Home
                    </Menu.Item>
                    <Menu.Item as='a'>
                        <Icon name='gamepad' />
                        Games
                    </Menu.Item>
                    </Sidebar>

                    <Card color="olive" animated='fade'>
                        <Image src={audiocard.attributes.image} /> 
                        <Card.Content
                            visible
                            onMouseOver={playRecording()}
                        >
                            <Card.Header>{audiocard.attributes.soundster}</Card.Header>
                            <Card.Meta>
                                <span className='date'>Created on {newDate}</span>
                            </Card.Meta>
                            <Card.Description>
                                ({audiocard.attributes.category})
                            </Card.Description>
                        </Card.Content>
                        <div className='icon-container'>
                            <Card.Content extra visible>
                                <Grid>
                                    <Grid.Row column={2} className='tag-button-container'>
                                        {getTags}
                                    </Grid.Row>
                                </Grid>
                            </Card.Content>
                            <Card.Content hidden>
                                <Button icon ="times circle" color='red' />
                            </Card.Content>
                        </div>
                    </Card>
                </Sidebar.Pushable>
        )
}

export default connect(null, {confirmDelete, deleteAudiocard})(Audiocard)
