import React, { PureComponent } from 'react';
import { Menu, Icon, Search, Button, Modal } from 'semantic-ui-react';
import { Link } from 'react-router-dom';
import Login from './components/Login'
import './App.css';
import './images/userIcon.png';


class NavBar extends PureComponent {
  constructor(props) {
    super()
    this.state = {
      deviceWidth: window.innerWidth,
      open: false
    }
  }

  //resizes based on device type
  componentDidMount() {
    window.addEventListener('resize', this.handleWindowSizeChange);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleWindowSizeChange);
  }

  handleWindowSizeChange = () => {
    this.setState({ deviceWidth: window.innerWidth });
  }

  //modal button open/close

  close = () => this.setState({open: false})
  open = () => this.setState({open: true})

  render(){
    //resizing
    let isMobile
    const setWidth = this.state.deviceWidth
    setWidth <= 750 ?  isMobile = true : isMobile = false


    let resizedIcon
    let navType
    if (isMobile) {
      resizedIcon = "small"
      navType = "navMobile"
    } else {
      resizedIcon = "huge"
      navType = "navDesktop"
    }

    return (
        <div className={navType}>
            <Menu secondary fixed="top" widths={4}>
                <Menu.Item><Icon size={resizedIcon} name='home' color='orange' /></Menu.Item>
                <Menu.Item>
                    <Search category />
                </Menu.Item>
                <Menu.Item>
                    <Link to='/recorder' ><Icon size={resizedIcon} name='microphone' color='olive' /></Link>
                </Menu.Item>
                <Menu.Item>
                    <Button.Group>
                        <Modal open={open} onClose={this.close}
                            size='small' 
                            dimmer='blurring' 
                            trigger={
                                <Button color='teal' onClick={this.open}>
                                    <Link to='/login'>Login</Link>
                                </Button>} 
                            closeIcon>
                            <Modal.Content> 
                                <Login /> 
                            </Modal.Content>
                            <Modal.Actions>
                                <Button type='submit' onClick={this.close}>Submit</Button>
                            </Modal.Actions>
                        </Modal>   
                        <Button.Or />
                        <Modal 
                            size='small' 
                            dimmer='blurring' 
                            trigger={<Button positive>Signup</Button>} 
                            closeIcon
                        />
                    </Button.Group>
                </Menu.Item>
            </Menu>
        </div>

    )

  }

}

export default NavBar;
