import React from 'react'
import {Icon, Label} from 'semantic-ui-react'

const Playcontroller = ({playing, playOrPause, sound, playCount}) => {

    const handleRewind = e => {
        console.log(sound.sourceNode.context.currentTime)
        if(sound){
            sound.pause()
            sound.sourceNode.context.currentTime = 0
        }
    }

    const handleFastforward = e => {
        if(sound){
            sound.stop()
        }
    }

    return(
        <div>
            <Label className="count" circular size='large'>
                {playCount}
            </Label> 
            <Icon 
                onClick={handleRewind}
                name='fast backward'
                size='small'
            />   
            <Icon 
                onClick={playOrPause} 
                name={playing ? 'pause circle outline':'play circle outline'} 
                style={{color: "#D8ECE8"}}
                count={playCount}
            />
            <Icon 
                onClick={handleFastforward}
                name='fast forward'
                size='small'
            />  
        </div>
    )

}

export default Playcontroller;
