import React from 'react'
import { Image, Card, Icon, List } from 'semantic-ui-react';

const Audiocard = ({audiocard}) => {

    let newDate = ''
    if (audiocard.attributes.created_at){
        let convertDate = audiocard.attributes.created_at.split('T')[0].split('-')
        newDate = new Date(convertDate).toDateString()
    }

    const playRecording = () => {
        audiocard.attributes.soundfile.play()
    }


    return (
            <Card className='audiocard-container' color="olive">
                <Image src={audiocard.attributes.image} /> 
                <Card.Content 
                    // onMouseOver={() => this.playRecording()}
                >
                    <Card.Header>{audiocard.attributes.soundster}</Card.Header>
                    <Card.Meta>
                        <span className='date'>Created on {newDate}</span>
                    </Card.Meta>
                    <Card.Description>
                        ({audiocard.attributes.category})
                    </Card.Description>
                    <Card.Content extra>
                        <Icon name='hashtag'>{audiocard.attributes.tags}</Icon>
                    </Card.Content>
                </Card.Content>
            </Card>

    )
}

export default Audiocard;
