import React from 'react'
import { connect } from 'react-redux'
import { logout } from '../actions/currentUser.js'
import { Button, Icon, Input, Form } from 'semantic-ui-react'
import { Link } from 'react-router-dom'
import { ReactMic } from 'react-mic'


export class Recorder extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        soundster: '',
        record: false
      }
    }


    startRecording = () => {
      this.setState({
        record: true
      });
    }

    stopRecording = () => {
      this.setState({
        record: false
      });
    }

    onData(recordedBlob) {
      console.log('chunk of real-time data is: ', recordedBlob);
    }

    onStop(recordedBlob) {
      console.log('recordedBlob is: ', recordedBlob);
    }



    render(){

        const rotateGears = () => {
            document.querySelector('.gear1').style.animation = "rotation 8s infinite linear"
        }

        return (
            <div className='mainRecContainer'>
                <div className='cassette'>
                    <div className='gear1'>
                        <span className='helper'></span>
                    </div>
                    <div className='gear2'>
                        <span className='helper'></span>
                    </div>
                    <div className='soundsterContainer'>
                        <Form className='soundster-input'>
                            <Form.Input onChange={this.handleChange}
                                placeholder="Who's the soundster?"
                                name='soundster'
                                value={this.state.soundster}
                            />
                        </Form>
                    </div>

                </div>
                <div classname='recordingContainer'>
                    <ReactMic
                        record={this.state.record}
                        className="sound-wave"
                        onStop={this.onStop}
                        strokeColor="#D4E6D7"
                        backgroundColor="#8FC6B4" 
                        duration={25}
                        />
                </div>
                <Button.Group icon>
                    <Button className='recordButton' color="olive" onclick={rotateGears}>
                        <Icon name='microphone' />
                        Record 
                    </Button>
                    <Button className='pauseButton'>
                        <Icon name='pause' />
                        Pause   
                    </Button>
                    <Button className='playButton'>
                        <Icon name='play' />
                        Play
                    </Button>
                    <Button className= 'saveButton' color="orange">
                        <Icon name='paper plane outline' />
                        Save
                    </Button>
                </Button.Group>
            </div>
        )
    }
}

export default Recorder
