import React from 'react'
import {Icon, Transition} from 'semantic-ui-react'
class Count extends React.Component{
    constructor(props){
        super(props)
        this.state={
            count: 0,
            visible: true
        }
    }

    playAndIncrement = e => (
        this.props.playSound(),
        this.setState({count: this.state.count + 1})
    )

    render(){
        console.log(this.props)
        return(
            <div>
                <Transition onClick={this.playAndIncrement}>
                    <Icon corner='bottom left' style={{background: "#F79C6D"}}>Play | Count:{this.state.count}</Icon>
                </Transition>
            </div>
        )
    }
}

export default Count
